console.log("Chrome Comm - Content script loaded on:",window.location.href);chrome.runtime.sendMessage({type:"CONTENT_SCRIPT_READY",url:window.location.href,timestamp:Date.now()},e=>{if(chrome.runtime.lastError){console.error("Error sending message:",chrome.runtime.lastError);return}console.log("Background acknowledged:",e)});chrome.runtime.onMessage.addListener((e,r,o)=>{switch(console.log("Content script received message:",e),e.type){case"GET_PAGE_INFO":o({success:!0,pageInfo:{url:window.location.href,title:document.title,timestamp:Date.now()}});break;default:o({success:!1,error:"Unknown message type"})}return!0});console.log("Chrome Comm - Content script initialization complete");
